product_data = json.loads(product_data)
product_data = product_data['RESPONSE']['products']
product_attr_dict = {}
for each_product in product_data:
    product_attr_dict[each_product['product_id']] = each_product

# print product_attr_dict

def enrich(each_product) :
    product_id_ = each_product['product_id']
    if product_id_ in product_attr_dict:
        product_data = product_attr_dict[product_id_]
    else :
        product_data = {}
    necessary_keys = ["title", "attributes"]
    for key in necessary_keys :
        if key in product_data :
            each_product[key] = product_data[key]
    return each_product

@app.route("/score")
def score():
    args = request.args
    global scorer
    global graph
    if scorer is None:
        resp = "model not init, Use to /init api to init the model"
        model_path = None
    else:
        with graph.as_default():
            if "clicked" not in args :
                clicked_products = []
            else :
                clicked_arg = args['clicked']
                clicked_products = clicked_arg.split(",")

            nn_version=None
            if "model_version" in args :
                nn_version = args["model_version"]

            resp = scorer.score(product_attr_dict.keys(), clicked_products, nn_version=nn_version)
            resp = map(enrich, resp)
            resp = {"products": resp}
            model_path = scorer.model_path

    body = {"REQUEST" : {"args" : args, "model_path" : model_path}, "RESPONSE" : resp}
    return jsonify(body)


